mapper.update:
UPDATE COMMU_BOARD
   SET COMMU_VIEWS = COMMU_VIEWS + 1
 WHERE COMMU_IDK = #{value}

mapper.delete:
DELETE FROM COMMENT_TABLE

	
 WHERE GROUP_IDK = #{commentIdk}
	
	
 WHERE COMMENT_IDK = #{commentIdk}

mapper.select:
SELECT COUNT(A.COMMU_IDK) AS CNT
  FROM COMMU_LIKES  A, COMMU_BOARD B
 WHERE A.COMMU_IDK = B.COMMU_IDK
   AND A.USER_ID = #{userId}
   AND A.COMMU_IDK = #{commuIdk}

SELECT COUNT(A.COMMU_IDK) AS CNT
  FROM COMMU_SCRAP  A, COMMU_BOARD B
 WHERE A.COMMU_IDK = B.COMMU_IDK
   AND A.USER_ID = #{userId}
   AND A.COMMU_IDK = #{commuIdk}

mapper.update:
UPDATE COMMU_BOARD
   SET COMMU_LIKES = COMMU_LIKES + 1
 WHERE COMMU_IDK = #{value}

UPDATE COMMU_BOARD
   SET COMMU_LIKES = COMMU_LIKES - 1
 WHERE COMMU_IDK = #{value}

mapper.delete:
DELETE FROM COMMU_LIKES
 WHERE USER_ID = #{userId}
   AND COMMU_IDK = #{commuIdk}

DELETE FROM COMMU_SCRAP
 WHERE USER_ID = #{userId}
   AND COMMU_IDK = #{commuIdk}

mapper.select:
SELECT COMMU_IDK, COMMU_TAB, USER_ID, COMMU_TITLE, COMMU_CONTENT, COMMU_VIEWS, COMMU_LIKES, COMMU_STATUS, REG_DATE, MOD_DATE, DELETE_DATE, FILE_CHECK, FILE_EXT FROM (SELECT ROWNUM AS RNUM, COMMU_IDK, COMMU_TAB, USER_ID, COMMU_TITLE, COMMU_CONTENT, COMMU_VIEWS, COMMU_LIKES, COMMU_STATUS, REG_DATE, MOD_DATE, DELETE_DATE, FILE_CHECK, FILE_EXT FROM (SELECT COMMU_IDK, NVL(COMMU_TAB,'') AS COMMU_TAB, NVL(USER_ID,'') AS USER_ID, NVL(COMMU_TITLE,'') AS COMMU_TITLE, NVL(COMMU_CONTENT,'') AS COMMU_CONTENT, NVL(COMMU_VIEWS,'') AS COMMU_VIEWS, NVL(COMMU_LIKES,'') AS COMMU_LIKES, NVL(COMMU_STATUS,'') AS COMMU_STATUS, TO_CHAR(REG_DATE,'YYYY.MM.DD') AS REG_DATE, TO_CHAR(MOD_DATE,'YYYY.MM.DD') AS MOD_DATE, TO_CHAR(DELETE_DATE,'YYYY.MM.DD') AS DELETE_DATE, NVL(FILE_CHECK,'N') AS FILE_CHECK, NVL(FILE_EXT, '') AS FILE_EXT FROM COMMU_BOARD WHERE USER_ID = #{userId}
AND COMMU_TITLE LIKE '%' || #{commuTitle} || '%'
ORDER BY COMMU_IDK DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT COUNT(COMMU_IDK) AS CNT
  FROM COMMU_SCRAP
 WHERE COMMU_IDK = #{value}

SELECT COUNT(COMMU_IDK) AS CNT
  FROM COMMU_LIKES
 WHERE COMMU_IDK = #{value}
