mapper.select:
SELECT PRODUCT_IDK, SHOP_TAB_CODE, SHOP_DETAIL_TAB_CODE, PRODUCT_CODE, SELLER_ID, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_BRAND_NAME, PRODUCT_DESCRIPTION, PRODUCT_FILE_EXT, PRODUCT_STATUS, REG_DATE, PRODUCT_VIEWS FROM(SELECT ROWNUM AS RNUM, PRODUCT_IDK, SHOP_TAB_CODE, SHOP_DETAIL_TAB_CODE, PRODUCT_CODE, SELLER_ID, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_BRAND_NAME, PRODUCT_DESCRIPTION, PRODUCT_FILE_EXT, PRODUCT_STATUS, REG_DATE, PRODUCT_VIEWS FROM(SELECT PRODUCT_IDK, NVL(SHOP_TAB_CODE, '') AS SHOP_TAB_CODE, NVL(SHOP_DETAIL_TAB_CODE, '') AS SHOP_DETAIL_TAB_CODE, NVL(PRODUCT_CODE, '') AS PRODUCT_CODE, NVL(SELLER_ID, '') AS SELLER_ID, NVL(PRODUCT_NAME, '') AS PRODUCT_NAME, NVL(PRODUCT_PRICE, '') AS PRODUCT_PRICE, NVL(PRODUCT_BRAND_NAME, '') AS PRODUCT_BRAND_NAME, NVL(PRODUCT_DESCRIPTION, '') AS PRODUCT_DESCRIPTION, NVL(PRODUCT_FILE_EXT, '') AS PRODUCT_FILE_EXT, NVL(PRODUCT_STATUS, '') AS PRODUCT_STATUS, NVL( TO_CHAR( REG_DATE, 'YYYY.MM.DD HH24:MI:SS'),'') AS REG_DATE, NVL(PRODUCT_VIEWS, '') AS PRODUCT_VIEWS FROM PRODUCT WHERE 1=1
AND SHOP_TAB_CODE = #{shopTabCode}
AND SHOP_DETAIL_TAB_CODE = #{shopDetailTabCode}
AND PRODUCT_BRAND_NAME LIKE '%' || #{productBrandName} || '%'
AND PRODUCT_NAME LIKE '%' || #{productName} || '%'
AND PRODUCT_PRICE LIKE '%' || #{productPrice} || '%'
AND PRODUCT_STATUS = #{productStatus}
AND REG_DATE LIKE '%' || #{regDate} || '%'
ORDER BY PRODUCT_PRICE DESC
ORDER BY PRODUCT_PRICE ASC
ORDER BY REG_DATE DESC
ORDER BY PRODUCT_CODE ASC
) )

SELECT DISTINCT PRODUCT_BRAND_NAME FROM PRODUCT WHERE 1=1 AND SHOP_TAB_CODE = #{shopTabCode}
AND SHOP_DETAIL_TAB_CODE = #{shopDetailTabCode}
ORDER BY PRODUCT_PRICE DESC
ORDER BY PRODUCT_PRICE ASC
ORDER BY REG_DATE DESC

SELECT PRODUCT_IDK ,SHOP_TAB_CODE ,SHOP_DETAIL_TAB_CODE ,PRODUCT_CODE ,SELLER_ID ,PRODUCT_NAME ,PRODUCT_PRICE ,PRODUCT_BRAND_NAME ,PRODUCT_DESCRIPTION ,PRODUCT_FILE_EXT ,PRODUCT_STATUS ,REG_DATE ,PRODUCT_VIEWS FROM(SELECT ROWNUM AS RNUM ,PRODUCT_IDK ,SHOP_TAB_CODE ,SHOP_DETAIL_TAB_CODE ,PRODUCT_CODE ,SELLER_ID ,PRODUCT_NAME ,PRODUCT_PRICE ,PRODUCT_BRAND_NAME ,PRODUCT_DESCRIPTION ,PRODUCT_FILE_EXT ,PRODUCT_STATUS ,REG_DATE ,PRODUCT_VIEWS FROM(SELECT PRODUCT_IDK ,NVL(SHOP_TAB_CODE, '') AS SHOP_TAB_CODE ,NVL(SHOP_DETAIL_TAB_CODE, '') AS SHOP_DETAIL_TAB_CODE ,NVL(PRODUCT_CODE, '') AS PRODUCT_CODE ,NVL(SELLER_ID, '') AS SELLER_ID ,NVL(PRODUCT_NAME, '') AS PRODUCT_NAME ,NVL(PRODUCT_PRICE, '') AS PRODUCT_PRICE ,NVL(PRODUCT_BRAND_NAME, '') AS PRODUCT_BRAND_NAME ,NVL(PRODUCT_DESCRIPTION, '') AS PRODUCT_DESCRIPTION ,NVL(PRODUCT_FILE_EXT, '') AS PRODUCT_FILE_EXT ,NVL(PRODUCT_STATUS, '') AS PRODUCT_STATUS ,NVL( TO_CHAR( REG_DATE, 'YYYY.MM.DD HH24:MI:SS'),'') AS REG_DATE ,NVL(PRODUCT_VIEWS, '') AS PRODUCT_VIEWS FROM PRODUCT WHERE 1=1
AND PRODUCT_NAME LIKE '%' || #{productName} || '%'
AND PRODUCT_STATUS = #{productStatus}
ORDER BY PRODUCT_VIEWS DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT COUNT(PRODUCT_IDK) FROM PRODUCT WHERE 1=1
AND PRODUCT_NAME LIKE '%' || #{productName} || '%'
AND PRODUCT_STATUS = #{productStatus}

     
 SELECT COUNT (PRODUCT_IDK) AS CNT
    FROM PRODUCT
    WHERE 1=1
       
     AND SHOP_TAB_CODE = #{shopTabCode}			
     
    
 	 AND SHOP_DETAIL_TAB_CODE = #{shopDetailTabCode}		
  

    
	 AND PRODUCT_BRAND_NAME LIKE '%' || #{productBrandName} || '%'

mapper.update:
UPDATE PRODUCT
   SET PRODUCT_VIEWS = PRODUCT_VIEWS + 1
 WHERE PRODUCT_IDK = #{value}

UPDATE CART
SET QUANTITY = QUANTITY + #{quantity}
WHERE USER_ID = #{userId} 
AND PRODUCT_IDK = #{productIdk}

mapper.delete:
DELETE FROM CART
WHERE	USER_ID = #{userId}
    AND PRODUCT_IDK = #{productIdk}
